table.sql:
CREATE TABLE `global_search_modules` (
					`module`  varchar(250) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' ,
					`active`  int(1) NULL DEFAULT NULL ,
					`sequence`  int(3) NULL,
					PRIMARY KEY (`module`)
					);

CREATE TABLE `global_search_fields` (
				`id`  int(11) NOT NULL AUTO_INCREMENT,
				`module`  varchar(50) NULL ,
				`fieldname`  varchar(250) NULL ,
				PRIMARY KEY (`id`)
				);
